import React, { useState } from 'react';
import './AddNewRule.css';

function AddNewRule({ onClose, onAddRule }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [programType, setProgramType] = useState('');
  const [mediaType, setMediaType] = useState('');
  const [disclaimer, setDisclaimer] = useState('');

  const handleAdd = async (e) => {
    e.preventDefault(); // Prevent the default form submission
    if (title && description && programType && mediaType && disclaimer) {
      const newRule = {
        rulename: title,
        media_type: mediaType,
        description,
        program_type: programType,
        disclaimer
      };
      try {
        const response = await fetch('http://127.0.0.1:8000/add_rule', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(newRule)
        });
        const data = await response.json();
        if (data.status === 'SUCCESS') {
          alert('Rule added successfully!');
          onAddRule(newRule);
          onClose();
        } else {
          alert('Failed to add rule: ' + data.data);
        }
      } catch (error) {
        console.error('Error adding rule:', error);
        alert('Error adding rule');
      }
    } else {
      alert('Please enter all fields');
    }
  };

  return (
    <div className="add-new-rule-overlay">
      <div className="add-new-rule">
        <div className="add-new-rule-header">
          <div>Add Rule</div>
          <button className="add-new-rule-close" onClick={onClose}>×</button>
        </div>
        <form className="add-new-rule-body" onSubmit={handleAdd}>
          <div className="add-new-rule-field">
            <label>Title</label>
            <input 
              type="text" 
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="Enter title" 
            />
          </div>
          <div className="add-new-rule-field">
            <label>Description</label>
            <input 
              type="text" 
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Enter description" 
            />
          </div>
          <div className="add-new-rule-field">
            <label>Disclaimer</label>
            <input 
              type="text" 
              value={disclaimer}
              onChange={(e) => setDisclaimer(e.target.value)}
              placeholder="Enter disclaimer" 
            />
          </div>
          <div className="add-new-rule-field-row">
            <div className="add-new-rule-field">
              <label>Program Type</label>
              <select 
                value={programType}
                onChange={(e) => setProgramType(e.target.value)}
              >
                <option value="">Select Program Type</option>
                <option value="IAP">IAP</option>
                <option value="Product">Product</option>
                <option value="NFO">NFO</option>
              </select>
            </div>
            <div className="add-new-rule-field">
              <label>Media Type</label>
              <select 
                value={mediaType}
                onChange={(e) => setMediaType(e.target.value)}
              >
                <option value="">Select Media Type</option>
                <option value="PDF/Image">PDF/Image</option>
                <option value="Video">Video</option>
              </select>
            </div>
          </div>
          <div className="add-new-rule-footer">
            <button type="button" className="add-new-rule-add" onClick={handleAdd}>Add</button>
            <button type="button" className="add-new-rule-cancel" onClick={onClose}>Cancel</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default AddNewRule;
