import React, { useState, useEffect } from 'react';
import './Rules.css';
import { FaEdit, FaTrash } from 'react-icons/fa';

function Rules({ onAddNew, onEditRule }) {
  const [rules, setRules] = useState([]);

  useEffect(() => {
    fetchRules();
  }, []);

  const fetchRules = async () => {
    try {
      const response = await fetch('http://127.0.0.1:8000/list_rules');
      const data = await response.json();
      if (data.status === 'SUCCESS') {
        setRules(data.data);
      } else {
        console.error('Failed to fetch rules:', data.data);
      }
    } catch (error) {
      console.error('Error fetching rules:', error);
    }
  };

  return (
    <div className="rules">
      <div className="rules-heading">
        <div>Rules</div>
        <div><button className="rules__add-new-button" onClick={onAddNew}>+ Add New</button></div>
      </div>
      <table className="rules__content-table">
        <thead>
          <tr>
            <th>Sr. No.</th>
            <th>Title</th>
            <th>Media Type</th>
            <th>Description</th>
            <th>Application Program</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {rules.length > 0 ? (
            rules.map((rule, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{rule[0]}</td>
                <td>{rule[1]}</td>
                <td>{rule[2]}</td>
                <td>{rule[3]}</td>
                <td>
                  <button className="rules__edit-button" onClick={() => onEditRule(rule)}><FaEdit /> Edit</button>
                  <button className="rules__delete-button"><FaTrash /> Delete</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6">No rules found</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default Rules;
