import React, { useState } from 'react';
import './AddNewProgram.css';

function AddNewProgram({ onClose }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [programType, setProgramType] = useState('');
  const [mediaType, setMediaType] = useState('');

  const handleAdd = () => {
    // Handle the add logic here
    onClose();
  };

  return (
    <div className="add-new-program-overlay">
      <div className="add-new-program">
        <h2>Add New Program</h2>
        <div className="add-new-program__form-group">
          <label>Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
        <div className="add-new-program__form-group">
          <label>Description</label>
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <div className="add-new-program__form-group">
          <label>Program Type</label>
          <input
            type="text"
            value={programType}
            onChange={(e) => setProgramType(e.target.value)}
          />
        </div>
        <div className="add-new-program__form-group">
          <label>Media Type</label>
          <input
            type="text"
            value={mediaType}
            onChange={(e) => setMediaType(e.target.value)}
          />
        </div>
        <div className="add-new-program__actions">
          <button onClick={handleAdd}>Add</button>
          <button onClick={onClose}>Cancel</button>
        </div>
      </div>
    </div>
  );
}

export default AddNewProgram;
