import React, { useState, useEffect } from 'react';
import './EditRule.css';

function EditRule({ onClose, rule, onUpdateRule }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [programType, setProgramType] = useState('');
  const [mediaType, setMediaType] = useState('');
  const [disclaimer, setDisclaimer] = useState('');

  useEffect(() => {
    if (rule) {
      setTitle(rule[0]); // Assuming 'rule[0]' is the title
      setDescription(rule[2]); // Assuming 'rule[2]' is the description
      setDisclaimer(rule[3]); // Assuming 'rule[3]' is the disclaimer
      setProgramType(rule[4]); // Assuming 'rule[4]' is the program type, make sure this index is correct
      setMediaType(rule[1]); // Assuming 'rule[1]' is the media type
    }
  }, [rule]);

  const handleEdit = () => {
    if (title && description && programType && mediaType && disclaimer) {
      const updatedRule = { rulename: title, media_type: mediaType, description, program_type: programType, disclaimer };
      onUpdateRule(updatedRule);
      onClose();
    } else {
      alert('Please enter all fields');
    }
  };

  {"Rule is: "}

  return (
    <div className="edit-rule-overlay">
      <div className="edit-rule">
        <div className="edit-rule-header">
          <div>Edit Rule</div>
          <button className="edit-rule-close" onClick={onClose}>×</button>
        </div>
        <div className="edit-rule-body">
          <div className="edit-rule-field">
            <label>Title</label>
            <input 
              type="text" 
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="Enter title" 
            />
          </div>
          <div className="edit-rule-field">
            <label>Description</label>
            <input 
              type="text" 
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Enter description" 
            />
          </div>
          <div className="edit-rule-field">
            <label>Disclaimer</label>
            <input 
              type="text" 
              value={disclaimer}
              onChange={(e) => setDisclaimer(e.target.value)}
              placeholder="Enter disclaimer" 
            />
          </div>
          <div className="edit-rule-field-row">
            <div className="edit-rule-field">
              <label>Program Type</label>
              <select 
                value={programType}
                onChange={(e) => setProgramType(e.target.value)}
              >
                <option value="">Select Program Type</option>
                <option value="IAP">IAP</option>
                <option value="Product">Product</option>
                <option value="NFO">NFO</option>
              </select>
            </div>
            <div className="edit-rule-field">
              <label>Media Type</label>
              <select 
                value={mediaType}
                onChange={(e) => setMediaType(e.target.value)}
              >
                <option value="">Select Media Type</option>
                <option value="PDF/Image">PDF/Image</option>
                <option value="Video">Video</option>
              </select>
            </div>
          </div>
        </div>
        <div className="edit-rule-footer">
          <button className="edit-rule-save" onClick={handleEdit}>Save</button>
          <button className="edit-rule-cancel" onClick={onClose}>Cancel</button>
        </div>
      </div>
    </div>
  );
}

export default EditRule;
